alias.send:
path_bushu = GetVariable("path_bushu");
	if path_bushu ~= nil or path_bushu > 0 then 
		SetVariable ("path_bushu",path_bushu - 1);
		SendNoEcho ("response bianli_wait 遍历下一步");
	SendNoEcho("unresponse bianli_wait");
		Execute("lanluoff");
EnableGroup("遍历",1);
    ResetTimer("bianli_time")
    EnableTimer("bianli_time", 1)
Execute("look")
	end
